class Solution:
    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        if k == 1:
            return [len(word) for word in words]
        if not words:
            return [0] * len(words)

        def is_common_prefix(prefix, words):
            for word in words:
                if not word.startswith(prefix):
                    return False
            return True

        def find_common_prefix(words):
            if len(words) == 1:
                return words[0]
            mid = len(words) // 2
            left_prefix = find_common_prefix(words[:mid])
            right_prefix = find_common_prefix(words[mid:])
            return find_common_prefix([left_prefix, right_prefix])

        prefix = find_common_prefix(words)
        return [len(prefix) if is_common_prefix(prefix, words) else 0 for _ in range(len(words))]

