from collections import Counter

class Solution:
    def numOfUnplacedFruits(self, fruits: List[int], baskets: List[int]) -> int:
        counter = Counter(fruits)
        n = len(counter)
        if n > len(baskets):
            return n - len(baskets)

        left, right = 0, 0
        max_fruits = 0
        while right < len(fruits):
            max_fruits = max(max_fruits, counter[fruits[right]])
            if right - left + 1 > max_fruits:
                counter[fruits[left]] -= 1
                if counter[fruits[left]] == 0:
                    del counter[fruits[left]]
                left += 1
            right += 1

        return n - len(counter)

class Solution:
    def numOfUnplacedFruits(self, fruits: List[int], baskets: List[int]) -> int:
        n = len(fruits)
        if n == 0:
            return 0

        i = 0
        j = 0
        capacity = sum(baskets)
        total = sum(fruits)
        count = 0
        while i < n:
            if capacity >= fruits[i]:
                capacity -= fruits[i]
                i += 1
            else:
                if j == n - 1:
                    return count
                if capacity >= fruits[j + 1]:
                    capacity -= fruits[j + 1]
                    j += 1
                else:
                    count += 1
                    capacity = sum(baskets)
        return count

