class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        prefix_sum = [0] * (n + 1)
        for i in range(n):
            prefix_sum[i + 1] = prefix_sum[i] + nums[i]

        for l, r, val in queries:
            prefix_sum[r + 1] -= val * (r - l + 1)
            prefix_sum[l] += val * (r - l + 1)

        min_k = float('inf')
        for i in range(n):
            if prefix_sum[i] < 0:
                return -1
            min_k = min(min_k, i + 1 - prefix_sum[i])

        return min_k if min_k != float('inf') else -1
