class Solution:
    def numberOfComponents(self, properties: List[List[int]], k: int) -> int:
        def union(x, y):
            root_x = find(x)
            root_y = find(y)
            if root_x != root_y:
                parent[root_y] = root_x

        def find(x):
            if parent[x] != x:
                parent[x] = find(parent[x])
            return parent[x]

        parent = {}
        for i in range(len(properties)):
            parent[i] = i

