class Solution:
    def reverseDegree(self, s: str) -> int:
        reversed_alphabet = 'zabcdefghijklmnopqrstuvwxyz'
        reversed_alphabet_index = {char: index for index, char in enumerate(reversed_alphabet)}
        reversed_alphabet_index_reverse = {index: char for char, index in reversed_alphabet_index.items()}

        reversed_string = s[::-1]
        reversed_string_index = {char: index + 1 for index, char in enumerate(reversed_string)}

        reverse_degree = sum(reversed_string_index[char] * reversed_alphabet_index_reverse[index] for index, char in reversed_string_index.items())

        return reverse_degree
