    """Returns True if a and b are coprime, False otherwise."""
    return math.gcd(a, b) == 1

def is_connected(n, edges):
    """Returns True if the graph with n vertices and edges is connected, False otherwise."""
    from collections import defaultdict
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)
    visited = set()
    stack = [1]
    while stack:
        node = stack.pop()
        if node not in visited:
            visited.add(node)
            stack.extend(graph[node] - visited)
    return len(visited) == n

def shortest_distances(n, edges):
    """Returns a list of shortest distances from vertex 1 to all other vertices."""
    from collections import defaultdict
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)
    distances = [-1] * (n + 1)
    distances[1] = 0
    queue = [1]
    while queue:
        node = queue.pop(0)
        for neighbor in graph[node]:
            if distances[neighbor] == -1:
                distances[neighbor] = distances[node] + 1
                queue.append(neighbor)
    return distances[2:]

def count_even_odd_distances(n, distances):
    """Returns the count of vertices with even and odd shortest distances from vertex 1."""
    even_count = 0
    odd_count = 0
    for distance in distances:
        if distance % 2 == 0:
            even_count += 1
        else:
            odd_count += 1
    return even_count, odd_count

def solve(N, P):
    """Returns the count of connected graphs satisfying the condition modulo P."""
    count = 0
    for M in range((N * (N - 1)) // 2, 0, -1):
        edges = [(i, j) for i in range(1, N + 1) for j in range(i + 1, N + 1) if (i, j) not in edges]
        for edge in edges:
            edges.remove(edge)
            if is_connected(N, edges):
                distances = shortest_distances(N, edges)
                even_count, odd_count = count_even_odd_distances(N, distances)
                if even_count == odd_count:
                    count = (count + 1) % P
                edges.append(edge)
    return count

