    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def prime_factors(n):
    """Return the prime factors of a number."""
    factors = []
    while n % 2 == 0:
        factors.append(2)
        n //= 2
    i = 3
    while i * i <= n:
        while n % i == 0:
            factors.append(i)
            n //= i
        i += 2
    if n > 2:
        factors.append(n)
    return factors

def modular_inverse(a, p):
    """Compute the modular inverse of a modulo p."""
    if a < 0:
        a += p
    if a == 0:
        return 0
    x, y, u, v = 0, 1, 1, 0
    while a != 0:
        q, r = divmod(p, a)
        p, a = a, r
        x, u = u, x - q * u
        y, v = v, y - q * v
    return x % p

def fermat_factorization(n):
    """Factorize a number using Fermat's factorization method."""
    if n % 2 == 0:
        return 2, n // 2
    d = 2
    while d * d <= n:
        if n % d == 0:
            return d, n // d
        d += 2
    return n - 1, 2

def chinese_remainder_theorem(a1, m1, a2, m2):
    """Solve the system of congruences using the Chinese Remainder Theorem."""
    if m1 == m2:
        return (a1 + a2) % m1
    if m1 < m2:
        m1, m2 = m2, m1
        a1, a2 = a2, a1
    if a1 % m2 == 0:
        return (a1 + m2 * chinese_remainder_theorem(a2 - a1 % m2, m2, m1, m2)) % m1
    if a2 % m1 == 0:
        return (a2 + m1 * chinese_remainder_theorem(a1 - a2 % m1, m1, m2, m1)) % m2
    s1 = modular_inverse(m1 // math.gcd(m1, m2), m2)
    s2 = modular_inverse(m2 // math.gcd(m1, m2), m1)
    return (a1 * s1 * m2 + a2 * s2 * m1) % (m1 * m2)

