def is_prime(n: int) -> bool:
    """Check if a number is prime."""
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def prime_factors(n: int) -> List[int]:
    """Return the prime factors of a number."""
    factors = []
    while n % 2 == 0:
        factors.append(2)
        n //= 2
    i = 3
    while i * i <= n:
        while n % i == 0:
            factors.append(i)
            n //= i
        i += 2
    if n > 2:
        factors.append(n)
    return factors

def count_even_distance_vertices(n: int, m: int, p: int) -> int:
    """Count the number of vertices with even distance from vertex 1."""
    factors = prime_factors(m)
    count = 0
    for factor in factors:
        if factor % 2 == 0:
            count += 1
    return count % p

def count_odd_distance_vertices(n: int, m: int, p: int) -> int:
    """Count the number of vertices with odd distance from vertex 1."""
    factors = prime_factors(m)
    count = 0
    for factor in factors:
        if factor % 2 != 0:
            count += 1
    return count % p

