from collections import *

def solve(N: int, P: int) -> List[int]:
    def is_prime(n: int) -> bool:
        if n <= 1:
            return False
        if n <= 3:
            return True
        if n % 2 == 0 or n % 3 == 0:
            return False
        i = 5
        while i * i <= n:
            if n % i == 0 or n % (i + 2) == 0:
                return False
            i += 6
        return True

    def is_power_of_two(n: int) -> bool:
        return n > 0 and (n & (n - 1)) == 0

    def count_even_distance_vertices(graph: List[List[int]], start: int) -> int:
        visited = [False] * N
        queue = deque([start])
        visited[start] = True
        even_distance_vertices = 0
        while queue:
            vertex = queue.popleft()
            for neighbor in graph[vertex]:
                if not visited[neighbor]:
                    visited[neighbor] = True
                    queue.append(neighbor)
                    if (vertex + 1) % 2 == (neighbor + 1) % 2:
                        even_distance_vertices += 1
        return even_distance_vertices

    def count_odd_distance_vertices(graph: List[List[int]], start: int) -> int:
        visited = [False] * N
        queue = deque([start])
        visited[start] = True
        odd_distance_vertices = 0
        while queue:
            vertex = queue.popleft()
            for neighbor in graph[vertex]:
                if not visited[neighbor]:
                    visited[neighbor] = True
                    queue.append(neighbor)
                    if (vertex + 1) % 2 != (neighbor + 1) % 2:
                        odd_distance_vertices += 1
        return odd_distance_vertices

    def count_valid_graphs(N: int, P: int) -> List[int]:
        if not is_prime(P):
            raise ValueError("P must be a prime number.")

        graph = [[] for _ in range(N)]
        for _ in range(N - 1):
            u, v = map(int, input().split())
            graph[u - 1].append(v - 1)
            graph[v - 1].append(u - 1)

        results = []
        for M in range((N * (N - 1)) // 2):
            even_distance = count_even_distance_vertices(graph, M)
            odd_distance = count_odd_distance_vertices(graph, M)
            if even_distance == odd_distance:
                results.append(even_distance % P)
            else:
                results.append(0)

        return results

