def solve(H, W, S):
    # YOUR SOLUTION HERE
    return "Yes" if is_rectangle_paintable(H, W, S) else "No"

def is_rectangle_paintable(H, W, S):
    # Check if there is at least one black cell
    has_black_cell = any('#' in row for row in S)
    if not has_black_cell:
        return False

    # Check if all black cells can form a rectangle
    for i in range(H):
        for j in range(W):
            if S[i][j] == '#':
                for a in range(i, H):
                    for b in range(j, W):
                        if S[a][b] == '#':
                            for c in range(j, W):
                                for d in range(i, H):
                                    if S[d][c] == '#':
                                        return True
    return False

def main():
    H, W = read_input()
    S = [read_input() for _ in range(H)]
    print(solve(H, W, S))

