from collections import *

def solve(N: int, X: int, V: List[int], A: List[int], C: List[int]) -> int:
    dp = [[0] * 4 for _ in range(X + 1)]
    for i in range(N):
        for j in range(X + 1):
            for k in range(4):
                if j - A[i] >= 0 and dp[j - A[i]][k] + C[i] <= X:
                    dp[j][k + 1] = max(dp[j][k + 1], dp[j - A[i]][k] + C[i])
    return min(dp[-1])

