import sys
from heapq import heappush, heappop

def main():
    input = sys.stdin.read
    data = input().split()
    N, K = int(data[0]), int(data[1])
    A = list(map(int, data[2:2*N+2]))
    B = list(map(int, data[2*N+2:3*N+2]))
    C = list(map(int, data[3*N+2:4*N+2]))

    heap = []
    for i in range(N):
        for j in range(N):
            for k in range(N):
                heappush(heap, A[i]*B[j] + B[j]*C[k] + C[k]*A[i])

    for _ in range(K-1):
        heappop(heap)

    print(heappop(heap))

if __name__ == "__main__":
    main()

def main():
    import sys
    input = sys.stdin.read
    data = input().split()
    N, K = int(data[0]), int(data[1])
    A = [int(x) for x in data[2:2+N]]
    B = [int(x) for x in data[2+N:2+2*N]]
    C = [int(x) for x in data[2+2*N:2+3*N]]
    print(largest(A, B, C, K))

def largest(A, B, C, K):
    import heapq
    heap = []
    for i in range(N):
        for j in range(N):
            for k in range(N):
                heapq.heappush(heap, A[i]*B[j] + B[j]*C[k] + C[k]*A[i])
    return heapq.nlargest(K, heap)[-1]

if __name__ == "__main__":
