import sys
from collections import defaultdict
from typing import List

def read_input() -> List[int]:
    """Reads the input from stdin and returns it as a list of integers."""
    return list(map(int, sys.stdin.read().split()))

def calculate_maximum_probability(dice_faces: List[List[int]]) -> float:
    """Calculates the maximum probability that two dice show the same number."""
    dice_counts = defaultdict(int)
    for faces in dice_faces:
        for face in faces:
            dice_counts[face] += 1

    max_probability = 0
    for count in dice_counts.values():
        max_probability = max(max_probability, count / len(dice_faces))

    return max_probability

def main():
    """Main function to read input, process it, and write output."""
    dice_faces = read_input()
    dice_faces = [dice_faces[i:i+6] for i in range(0, len(dice_faces), 6)]
    result = calculate_maximum_probability(dice_faces)
    print(result)

if __name__ == "__main__":
    main()
