    edges = [input() for _ in range(N)]
    return N, edges

def shortest_palindrome_path(N, edges):
    graph = [[] for _ in range(N + 1)]
    for i in range(1, N + 1):
        for j in range(1, N + 1):
            if edges[i - 1][j - 1] != '-':
                graph[i].append(j)

    def dfs(node, path, visited):
        if node == N:
            return path
        visited.add(node)
        for neighbor in graph[node]:
            if neighbor not in visited:
                result = dfs(neighbor, path + [neighbor], visited)
                if result is not None:
                    return result
        visited.remove(node)
        return None

    def is_palindrome(s):
        return s == s[::-1]

    for i in range(1, N + 1):
        for j in range(1, N + 1):
            if is_palindrome(''.join([edges[i - 1][j - 1], edges[j - 1][i - 1]])):
                path = dfs(i, [i], set())
                if path is not None:
                    return len(path) - 1
    return -1

def write_output(N, results):
    for i in range(1, N + 1):
        print(*results[i - 1], sep=' ')

