    edges = [input() for _ in range(N)]
    return N, edges

def is_palindrome(s):
    return s == s[::-1]

def shortest_palindrome_path(N, edges):
    graph = [[] for _ in range(N)]
    for i in range(N):
        for j in range(N):
            if edges[i][j] != '-':
                graph[i].append(j)

    def dfs(i, j, path, visited):
        if i == j:
            if is_palindrome(''.join(path)):
                return len(path)
            return float('inf')
        if (i, j) in visited:
            return float('inf')
        visited.add((i, j))
        for neighbor in graph[i]:
            if neighbor not in visited:
                result = dfs(neighbor, j, path + [edges[i][neighbor]], visited)
                if result != float('inf'):
                    return result
        visited.remove((i, j))
        return float('inf')

    shortest_paths = []
    for i in range(N):
        for j in range(N):
            if i != j:
                shortest_paths.append(dfs(i, j, [edges[i][j]], set([(i, j)])))

    return min(shortest_paths) if shortest_paths else -1

def write_output(N, shortest_paths):
    for i in range(N):
        for j in range(N):
            if i != j:
                print(shortest_paths[i][j], end=' ')
        print()

