def read_input():
    N = int(input())
    edges = [input() for _ in range(N)]
    return N, edges

def find_shortest_palindrome_path(N, edges):
    graph = [[] for _ in range(N + 1)]
    for i in range(1, N + 1):
        for j in range(1, N + 1):
            if edges[i - 1][j - 1] != '-':
                graph[i].append(j)

    def dfs(node, path, visited, shortest_path, shortest_length):
        if node == N:
            if path[::-1] == path:
                if len(path) < shortest_length[0]:
                    shortest_path[0] = path[:]
                    shortest_length[0] = len(path)
            return

        for neighbor in graph[node]:
            if neighbor not in visited:
                visited.add(neighbor)
                dfs(neighbor, path + [neighbor], visited, shortest_path, shortest_length)
                visited.remove(neighbor)

    shortest_path = [-1] * (N + 1)
    shortest_length = [float('inf')]
    dfs(1, [1], {1}, shortest_path, shortest_length)

    if shortest_path[N] == -1:
        return [-1] * N

    return shortest_path[1:]

