            if edges[i - 1][j - 1] != '-':
                graph[i].append(j)

    def dfs(node, path, visited, shortest_path, shortest_length):
        if node == N:
            if path[::-1] == path:
                if len(path) < shortest_length[0]:
                    shortest_path[0] = path[:]
                    shortest_length[0] = len(path)
            return

        for neighbor in graph[node]:
