def read_input():
    n = int(input())
    edges = [tuple(map(int, input().split())) for _ in range(n - 1)]
    return n, edges

def find_alkane(n, edges):
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    def dfs(node, parent):
        if node in visited:
            return False
        visited.add(node)
        degree[node] = 1 if parent is None else 2
        for neighbor in graph[node]:
            if neighbor != parent:
                if not dfs(neighbor, node):
                    return False
                degree[node] += degree[neighbor]
        return True

    visited = set()
    degree = [0] * (n + 1)
    for node in range(1, n + 1):
        if node not in visited:
            if not dfs(node, None):
                return -1
    return max(degree) if max(degree) in [1, 4] else -1

def main():
    n, edges = read_input()
    result = find_alkane(n, edges)
    print(result)

