import sys
from collections import defaultdict

def read_input():
    input = sys.stdin.read
    data = input().split()
    N = int(data[0])
    edges = defaultdict(list)
    for i in range(N - 1):
        A, B = map(int, data[2 * i + 1:2 * i + 3])
        edges[A].append(B)
        edges[B].append(A)
    return N, edges

def find_alkane(N, edges):
    def dfs(node, parent):
        if node in visited:
            return False
        visited.add(node)
        children = edges[node]
        if len(children) == 1 and children[0] == parent:
            return True
        if len(children) == 3:
            return dfs(children[0], node) and dfs(children[1], node) and dfs(children[2], node)
        if len(children) == 4:
            return dfs(children[0], node) and dfs(children[1], node) and dfs(children[2], node) and dfs(children[3], node)
        return False

    visited = set()
    for node in range(1, N + 1):
        if node not in visited:
            if dfs(node, None):
                return True
    return False

def main():
    N, edges = read_input()
    if find_alkane(N, edges):
        print(len(edges))
    else:
        print(-1)

if __name__ == "__main__":
