        queries.append((A, B, Y, C, D, Z))

    graph = defaultdict(dict)
    for i in range(1, H + 1):
        for j in range(1, W + 1):
            if i == 1:
                graph[(i, j)][(i, j + 1)] = F[i * W + j - 1]
            elif i == H:
                graph[(i, j)][(i, j - 1)] = F[i * W + j - 1]
            elif j == 1:
                graph[(i, j)][(i - 1, j)] = F[(i - 1) * W + j - 1]
            elif j == W:
                graph[(i, j)][(i + 1, j)] = F[(i + 1) * W + j - 1]
            else:
                graph[(i, j)][(i - 1, j)] = F[(i - 1) * W + j - 1]
                graph[(i, j)][(i + 1, j)] = F[(i + 1) * W + j - 1]
                graph[(i, j)][(i, j - 1)] = F[i * W + j - 1]
                graph[(i, j)][(i, j + 1)] = F[i * W + j - 1]

