import sys
from collections import defaultdict
from heapq import heappush, heappop

def dijkstra(graph, start, end):
    distances = defaultdict(lambda: float('inf'))
    distances[start] = 0
    pq = [(0, start)]
    visited = set()

    while pq:
        current_distance, current_node = heappop(pq)
        if current_node in visited:
            continue
        visited.add(current_node)

        if current_node == end:
            return current_distance

        for neighbor, weight in graph[current_node].items():
            distance = current_distance + weight
            if distance < distances[neighbor]:
                distances[neighbor] = distance
                heappush(pq, (distance, neighbor))

    return float('inf')

def main():
    input = sys.stdin.read
    data = input().split()
    H, W = int(data[0]), int(data[1])
    F = [list(map(int, input().split())) for _ in range(H)]
    Q = int(input())
    queries = [tuple(map(int, input().split())) for _ in range(Q)]

    graph = defaultdict(dict)
    for i in range(H):
        for j in range(W):
            if F[i][j] > 0:
                graph[(i, j)][(i - 1, j)] = 1 if i > 0 else float('inf')
                graph[(i, j)][(i + 1, j)] = 1 if i < H - 1 else float('inf')
                graph[(i, j)][(i, j - 1)] = 1 if j > 0 else float('inf')
                graph[(i, j)][(i, j + 1)] = 1 if j < W - 1 else float('inf')

    for A, B, Y, C, D, Z in queries:
        path = dijkstra(graph, (A, B), (C, D))
        if path == float('inf'):
            print(-1)
        else:
            print(path)

