    H, W = int(data[0]), int(data[1])
    F = []
    for i in range(H):
        F.append([int(x) for x in input().split()])
    Q = int(input())
    queries = []
    for i in range(Q):
        A, B, Y, C, D, Z = [int(x) for x in input().split()]
        queries.append((A, B, Y, C, D, Z))
    return H, W, F, Q, queries

def min_stairs(H, W, F, Q, queries):
    adj = defaultdict(list)
    for i in range(H):
        for j in range(W):
            if i > 0:
                adj[(i, j)].append((i - 1, j))
            if i < H - 1:
                adj[(i, j)].append((i + 1, j))
            if j > 0:
                adj[(i, j)].append((i, j - 1))
            if j < W - 1:
                adj[(i, j)].append((i, j + 1))

    def dfs(i, j, target, visited, path):
        if (i, j) in visited:
            return False
        if (i, j) == target:
            return True
        visited.add((i, j))
        path.append((i, j))
        for ni, nj in adj[(i, j)]:
            if dfs(ni, nj, target, visited, path):
                return True
        path.pop()
        return False

    def bfs(i, j, target, visited, path):
        queue = [(i, j)]
        visited.add((i, j))
        path.append((i, j))
        while queue:
            i, j = queue.pop(0)
            if (i, j) == target:
                return True
            for ni, nj in adj[(i, j)]:
                if (ni, nj) not in visited:
                    visited.add((ni, nj))
                    path.append((ni, nj))
                    queue.append((ni, nj))
        return False

    def min_stairs_helper(i, j, target, visited, path, method):
        if method == 'dfs':
            return dfs(i, j, target, visited, path)
        elif method == 'bfs':
            return bfs(i, j, target, visited, path)

    results = []
    for A, B, Y, C, D, Z in queries:
        visited = set()
        path = []
        if min_stairs_helper(A, B, (C, D), visited, path, 'dfs'):
            results.append(len(path) - 1)
        else:
            results.append(len(path) - 1)
    return results

H, W, F, Q, queries = read_input()
results = min_stairs(H, W, F, Q, queries)
for result in results:
    print(result)

