    cost = [0] * (N + 1)
    parent = [0] * (N + 1)

    def dfs(node, parent_node):
        visited[node] = True
        for neighbor in graph[node]:
            if not visited[neighbor]:
                parent[neighbor] = node
                cost[neighbor] = cost[node] + 1
                dfs(neighbor, node)

