        while node != 0:
            parent_node = parent[node]
            graph[parent_node].append(node)
            graph[node].remove(parent_node)
            node = parent_node

    def dfs_reverse(node, parent_node):
        visited[node] = True
        for neighbor in graph[node]:
            if not visited[neighbor]:
                parent[neighbor] = node
                cost[neighbor] = cost[node] + 1
                dfs_reverse(neighbor, node)

    dfs_reverse(N, 0)

    reverse_edges(N)

    total_cost = 0
    current_node = N
    while current_node != 1:
        if cost[current_node] == cost[parent[current_node]]:
            reverse_edges(current_node)
            total_cost += X
        else:
            total_cost += 1
        current_node = parent[current_node]

    return total_cost

