import sys
from collections import defaultdict

def read_input():
    return map(int, sys.stdin.read().split())

def solve(N, M, edges):
    graph = defaultdict(list)
    for u, v, w in edges:
        graph[u].append((v, w))
        graph[v].append((u, w))

    def dfs(node, parent, path, visited):
        if node in visited:
            return
        visited.add(node)
        path.append(node)
        if node == N:
            return path
        for neighbor, weight in graph[node]:
            if neighbor != parent:
                result = dfs(neighbor, node, path, visited)
                if result:
                    return result
        path.pop()
        return None

    visited = set()
    path = []
    return dfs(1, None, path, visited)

def main():
    N, M = read_input()
    edges = [read_input() for _ in range(M)]
    result = solve(N, M, edges)
    print(result[-1])

if __name__ == "__main__":
