def solve(N, M, edges):
    graph = defaultdict(list)
    for u, v, w in edges:
        graph[u].append((v, w))
        graph[v].append((u, w))

    def dfs(node, parent, path, visited, xor_path):
        visited[node] = True
        path.append(node)
        if node == N:
            xor_path.append(xor_path[-1])
            return True
        for neighbor, weight in graph[node]:
            if neighbor != parent and not visited[neighbor]:
                if dfs(neighbor, node, path, visited, xor_path ^ weight):
                    return True
        path.pop()
        return False

    visited = [False] * (N + 1)
    path = []
    xor_path = [0]
    dfs(1, -1, path, visited, xor_path)
    return xor_path[-1]

