def solve(A):
    N = len(A)
    distinct_counts = [set()] * N
    for i in range(N):
        distinct_counts[i] = set(A[:i+1])
    max_sum = 0
    for i in range(1, N):
        distinct_counts[i] = distinct_counts[i-1].union(set(A[i:]))
        max_sum = max(max_sum, len(distinct_counts[i-1]), len(distinct_counts[i]))
    return max_sum

