def read_input() -> Tuple[int, int, List[Tuple[int, int]]]:
    N, K = map(int, sys.stdin.readline().split())
    edges = []
    for _ in range(N - 1):
        u, v = map(int, sys.stdin.readline().split())
        edges.append((u, v))
    return N, K, edges

