    return len(set(path)) == len(path) and path[0] != path[-1]

def find_path(graph: defaultdict, start: int, end: int, path: List[int] = []) -> List[int]:
    if start == end:
        return path + [end]
    for neighbor in graph[start]:
        if neighbor not in path:
            result = find_path(graph, neighbor, end, path + [start])
            if result:
                return result
    return []

def decompose_tree(N: int, K: int, edges: List[Tuple[int, int]]) -> bool:
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    for i in range(1, N + 1):
        if i not in graph:
            return False

    for i in range(1, N + 1):
        if len(graph[i]) > 2:
            return False

    for i in range(1, N + 1):
        if len(graph[i]) == 1:
            start = i
            break

    path = find_path(graph, start, start)
    if not is_valid_path(path):
        return False

    for i in range(1, N + 1):
        if i not in path:
            return False

    return True

def main():
    N, K, edges = read_input()
    if decompose_tree(N, K, edges):
        print("Yes")
    else:
        print("No")

