def distinct_counts(A):
    distinct_counts = {}
    for num in A:
        if num in distinct_counts:
            distinct_counts[num] += 1
        else:
            distinct_counts[num] = 1
    return distinct_counts

def max_distinct_counts(A):
    max_count = 0
    for i in range(len(A) - 1):
        for j in range(i + 1, len(A)):
            distinct_counts_i = distinct_counts(A[:i + 1])
            distinct_counts_j = distinct_counts(A[i + 1:j + 1])
            distinct_counts_k = distinct_counts(A[j + 1:])
            max_count = max(max_count, len(distinct_counts_i) + len(distinct_counts_j) + len(distinct_counts_k))
    return max_count

