    input = sys.stdin.read
    data = input().split()
    N = int(data[0])
    A = [int(data[i]) for i in range(1, N+1)]
    print(greatest_non_repeating(A))

def greatest_non_repeating(A):
    if len(A) == 1:
        return A[0]
    A.sort()
    for i in range(len(A) - 1):
        if A[i] != A[i+1]:
            return A[i+1]
    return -1

