import sys
from collections import defaultdict

def read_input():
    input = sys.stdin.read
    data = input().split()
    N, M = map(int, data)
    edges = []
    for _ in range(M):
        U, V = map(int, input().split())
        edges.append((U, V))
    return N, M, edges

def is_odd_cycle(graph, start, visited, parent):
    visited[start] = True
    stack = [(start, 0)]
    while stack:
        node, depth = stack.pop()
        if depth % 2 == 0:
            if node in graph[node]:
                return True
        else:
            if node in graph[node]:
                return True
        for neighbor in graph[node]:
            if not visited[neighbor]:
                visited[neighbor] = True
                stack.append((neighbor, depth + 1))
    return False

def find_odd_cycle(graph):
    visited = [False] * (len(graph) + 1)
    parent = [-1] * (len(graph) + 1)
    for node in range(1, len(graph) + 1):
        if not visited[node]:
            if is_odd_cycle(graph, node, visited, parent):
                return True
    return False

def is_connected(graph):
    visited = [False] * (len(graph) + 1)
    stack = [1]
    visited[1] = True
    while stack:
        node = stack.pop()
        for neighbor in graph[node]:
            if not visited[neighbor]:
                visited[neighbor] = True
                stack.append(neighbor)
    return all(visited)

def solve(N, M, edges):
    graph = defaultdict(list)
    for U, V in edges:
        graph[U].append(V)
        graph[V].append(U)

    if is_connected(graph):
        return "Takahashi"

    if find_odd_cycle(graph):
        return "Takahashi"

    return "Aoki"

def main():
    N, M, edges = read_input()
    print(solve(N, M, edges))

if __name__ == "__main__":
    main()
