def read_input():
    input = sys.stdin.read
    data = input().split()
    N = int(data[0])
    M = int(data[1])
    edges = []
    for _ in range(M):
        u, v = map(int, input().split())
        edges.append((u, v))
    return N, M, edges

def min_edges_to_forest(N, M, edges):
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    visited = set()
    def dfs(node):
        visited.add(node)
        for neighbor in graph[node]:
            if neighbor not in visited:
                dfs(neighbor)

    components = 0
    for node in range(1, N + 1):
        if node not in visited:
            dfs(node)
            components += 1

    return components - 1

def main():
    N, M, edges = read_input()
    result = min_edges_to_forest(N, M, edges)
    print(result)

if __name__ == "__main__":
