import sys
from collections import defaultdict

def solve(N, S, T):
    if S == T:
        return 0

    char_count = defaultdict(int)
    for char in S:
        char_count[char] += 1

    for char in T:
        char_count[char] -= 1

    if any(count < 0 for count in char_count.values()):
        return -1

    return sum(abs(count) for count in char_count.values())

def main():
    input = sys.stdin.read
    data = input().split()
    N = int(data[0])
