import sys
from collections import deque

def read_input():
    return tuple(map(int, sys.stdin.readline().split()))

def write_output(output):
    print(output, file=sys.stdout)

def main():
    H, W = read_input()
    grid = [sys.stdin.readline().strip() for _ in range(H)]
    A, B, C, D = read_input()

    # Convert the grid to a list of lists for easier manipulation
    grid = [list(row) for row in grid]

    # Directions for moving and kicking
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]

    # BFS to find the shortest path
    def bfs(start, end):
        queue = deque([(start, 0)])
        visited = set()
        visited.add(start)

        while queue:
            (x, y), steps = queue.popleft()

            if (x, y) == end:
                return steps

            for dx, dy in directions:
                nx, ny = x + dx, y + dy
                if 0 <= nx < H and 0 <= ny < W and grid[nx][ny] == '.' and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append(((nx, ny), steps + 1))

    # Find the shortest path from (A, B) to (C, D)
    path = bfs((A - 1, B - 1), (C - 1, D - 1))

    # Perform front kicks to reach the fish shop
    front_kicks = 0
    for i in range(len(path) - 1):
        x1, y1 = path[i]
        x2, y2 = path[i + 1]

        # Calculate the direction of the front kick
        dx, dy = x2 - x1, y2 - y1
        if dx == 0:
            direction = 'right' if dy > 0 else 'left'
        elif dy == 0:
            direction = 'down' if dx > 0 else 'up'
        else:
            direction = 'diagonal'

        # Perform the front kick
        if direction == 'right':
            for j in range(2):
                if grid[x1 + j][y1] == '#':
                    grid[x1 + j][y1] = '.'
                    front_kicks += 1
        elif direction == 'left':
            for j in range(2):
                if grid[x1 - j][y1] == '#':
                    grid[x1 - j][y1] = '.'
                    front_kicks += 1
        elif direction == 'down':
            for j in range(2):
                if grid[x1][y1 + j] == '#':
                    grid[x1][y1 + j] = '.'
                    front_kicks += 1
        elif direction == 'up':
            for j in range(2):
                if grid[x1][y1 - j] == '#':
                    grid[x1][y1 - j] = '.'
                    front_kicks += 1
        else:
            # Diagonal front kick
            for j in range(2):
                if grid[x1 + j][y1 + j] == '#':
                    grid[x1 + j][y1 + j] = '.'
                    front_kicks += 1
                if grid[x1 + j][y1 - j] == '#':
                    grid[x1 + j][y1 - j] = '.'
                    front_kicks += 1
                if grid[x1 - j][y1 + j] == '#':
                    grid[x1 - j][y1 + j] = '.'
                    front_kicks += 1
                if grid[x1 - j][y1 - j] == '#':
                    grid[x1 - j][y1 - j] = '.'
                    front_kicks += 1

    write_output(front_kicks)

