    grid = [list(row) for row in S]

    # Directions for movement and front kick
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    front_kick_directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]

    # BFS to find the shortest path
    def bfs(start, end):
        queue = deque([(start, 0)])
        visited = set()
        visited.add(start)

        while queue:
            (x, y), steps = queue.popleft()

            if (x, y) == end:
                return steps

            for dx, dy in directions:
                nx, ny = x + dx, y + dy
                if 1 <= nx <= H and 1 <= ny <= W and grid[nx - 1][ny - 1] == '.' and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append(((nx, ny), steps + 1))

        return float('inf')

    # BFS to find the shortest path after front kicks
    def bfs_after_front_kicks(start, end):
        queue = deque([(start, 0)])
        visited = set()
        visited.add(start)

        while queue:
            (x, y), steps = queue.popleft()

            if (x, y) == end:
