def is_400_number(n):
    """Check if a number is a 400 number."""
    factors = {}
    for i in range(2, int(n**0.5) + 1):
        while n % i == 0:
            if i in factors:
                factors[i] += 1
            else:
                factors[i] = 1
            n //= i
    if n > 1:
        if n in factors:
            factors[n] += 1
        else:
            factors[n] = 1
    return len(factors) == 2 and all(v % 2 == 0 for v in factors.values())

def largest_400_number(n):
    """Find the largest 400 number not exceeding n."""
    for i in range(n, 0, -1):
        if is_400_number(i):
            return i
    return None

