import sys
from collections import defaultdict

def read_input():
    input = sys.stdin.read
    data = input().split()
    N, K = map(int, data)
    cakes = []
    for _ in range(N):
        X, Y, Z = map(int, input().split())
        cakes.append((X, Y, Z))
    return N, K, cakes

def max_pair_price(N, K, cakes):
    cake_dict = defaultdict(list)
    for i, (X, Y, Z) in enumerate(cakes):
        cake_dict[X].append((i, X))
        cake_dict[Y].append((i, Y))
        cake_dict[Z].append((i, Z))

    max_prices = [0] * N
    for i, (X, Y, Z) in enumerate(cakes):
        max_prices[i] = max(X + Y, X + Z, Y + Z)

    for i in range(N):
        for j in range(i + 1, N):
            if (max_prices[i] + max_prices[j]) > max_prices[i] + max_prices[j]:
                max_prices[i], max_prices[j] = max_prices[j], max_prices[i]

    pairs = []
    for i in range(N):
        for j in range(i + 1, N):
            if (max_prices[i] + max_prices[j]) > max_prices[i] + max_prices[j]:
                pairs.append((i, j))

    return pairs

def write_output(pairs):
    for i, j in pairs:
        print(i + 1, j + 1)

