    x = [0] * N
    total_cost = 0
    for L, R, op in operations:
        if op == 0:
            continue
        if op == 1:
            total_cost += 1
            for j in range(L, R + 1):
                x[j - 1] = 1
        elif op == 2:
            total_cost += 1
            for j in range(L, R + 1):
                x[j - 1] = 0
    return total_cost, x

