def compute_sum(N, p, A):
    from collections import defaultdict
    from functools import reduce
    from operator import add
    from typing import List

    def mod_pow(x: int, y: int, p: int) -> int:
        """Compute x^y % p."""
        res = 1
        x = x % p
        while y > 0:
            if (y % 2) == 1:
                res = (res * x) % p
            y = y >> 1
            x = (x * x) % p
        return res

    def mod_inverse(x: int, p: int) -> int:
        """Compute the modular inverse of x modulo p."""
        return pow(x, p - 2, p)

    def mod_sum(A: List[List[int]], p: int) -> List[List[int]]:
        """Compute the sum of all possible B^p modulo p."""
        N = len(A)
        mod = 10**9 + 7
        mod_inverse = lambda x: mod_inverse(x, mod)
        mod_pow = lambda x, y: mod_pow(x, y, mod)

        # Compute the number of zeros in A
        num_zeros = sum(row.count(0) for row in A)

        # Compute the sum of all possible B^p modulo p
        sum_B_p = [[0] * N for _ in range(N)]
        for i in range(N):
            for j in range(N):
                if A[i][j] == 0:
                    for k in range(num_zeros):
                        sum_B_p[i][j] += mod_pow(k + 1, p, mod)
                        sum_B_p[i][j] %= mod
                else:
                    sum_B_p[i][j] += mod_pow(A[i][j], p, mod)
                    sum_B_p[i][j] %= mod

