    edges = [tuple(map(int, data[4:6])) for _ in range(M)]
    return N, M, S, T, edges

def find_path(graph: Dict[int, List[int]], start: int, end: int) -> List[int]:
    """Finds a path from start to end in the graph."""
    visited = set()
    stack = [start]
    parent = {start: None}
    while stack:
        node = stack.pop()
        if node == end:
            path = []
            while node is not None:
                path.append(node)
                node = parent[node]
            return path[::-1]
        if node not in visited:
            visited.add(node)
            stack.extend(graph[node] - visited)
            parent[node] = stack[-1]
    return []

def find_shortest_path(graph: Dict[int, List[int]], start: int, end: int) -> int:
    """Finds the shortest path from start to end in the graph."""
    visited = set()
    queue = [(start, 0)]
    parent = {start: None}
    while queue:
        node, distance = queue.pop(0)
        if node == end:
            path = []
            while node is not None:
                path.append(node)
                node = parent[node]
            return distance, path[::-1]
