def solve(N, M, S, T, edges):
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    visited = [False] * (N + 1)
    parent = [None] * (N + 1)
    visited[S] = True
    parent[S] = None
    queue = [S]

    while queue:
        u = queue.pop(0)
        for v in graph[u]:
            if not visited[v]:
                visited[v] = True
                parent[v] = u
                queue.append(v)

    path = []
    while T is not None:
        path.append(T)
        T = parent[T]

    path.reverse()

    if path[-1] == S:
        return len(path) - 1

    return -1

