import sys
from collections import defaultdict, deque

def read_input():
    input = sys.stdin.read
    data = input().split()
    N, M, S, T = map(int, data)
    edges = defaultdict(list)
    for _ in range(M):
        u, v = map(int, input().split())
        edges[u].append(v)
        edges[v].append(u)
    return N, M, S, T, edges

def bfs(N, S, T, edges):
    visited = [False] * (N + 1)
    queue = deque([(S, 0)])
    while queue:
        node, moves = queue.popleft()
        if node == T:
            return moves
        if visited[node]:
            continue
        visited[node] = True
        for neighbor in edges[node]:
            if not visited[neighbor]:
                queue.append((neighbor, moves + 1))
    return -1

def main():
    N, M, S, T, edges = read_input()
    result = bfs(N, S, T, edges)
    print(result)

