def find_min_weight_path(graph, start, end, visited):
    if start == end:
        return 0
    visited.add(start)
    for neighbor in graph[start]:
        if neighbor not in visited:
            weight = graph[start][neighbor]
            path_weight = weight + find_min_weight_path(graph, neighbor, end, visited)
            if path_weight < min_weight:
                min_weight = path_weight
    visited.remove(start)
    return min_weight

