def find_min_weight_path(G, s, t):
    visited = set()
    stack = [(s, 0)]
    while stack:
        node, weight = stack.pop()
        if node == t:
            return weight
        if node in visited:
            continue
        visited.add(node)
        for neighbor in G[node]:
            if neighbor not in visited:
                stack.append((neighbor, weight + G[node][neighbor]))
    return -1

