    G = {i: set() for i in range(1, N + 1)}
    for L, R in pairs:
        for i in range(L, R + 1):
            for j in range(L, R + 1):
                if i != j:
                    G[i].add(j)
                    G[j].add(i)

    for s, t in queries:
        print(find_min_weight_path(G, s, t))

