from typing import List, Tuple

def read_input() -> Tuple[int, List[int], List[Tuple[int, int]], List[Tuple[int, int]]]:
    N = int(input())
    W = list(map(int, input().split()))
    L = []
    R = []
    for _ in range(N):
        l, r = map(int, input().split())
        L.append(l)
        R.append(r)
    Q = int(input())
    queries = []
    for _ in range(Q):
        s, t = map(int, input().split())
        queries.append((s, t))
    return N, W, L, R, queries

def find_min_weight_path(G: defaultdict, s: int, t: int) -> int:
    visited = set()
    stack = [(s, 0)]
    while stack:
        node, weight = stack.pop()
        if node == t:
            return weight
        if node in visited:
            continue
        visited.add(node)
        for neighbor, weight in G[node]:
            stack.append((neighbor, weight + weight))
    return -1

def main():
    N, W, L, R, queries = read_input()
    G = defaultdict(list)
    for i in range(N):
        for j in range(i + 1, N):
            if not (L[i] <= L[j] <= R[i] or L[j] <= L[i] <= R[j]):
                G[i].append((j, W[j]))
                G[j].append((i, W[i]))

    for s, t in queries:
        print(find_min_weight_path(G, s, t))

if __name__ == "__main__":
