    s = [None] * Q
    t = [None] * Q
    for i in range(Q):
        s[i], t[i] = read_input()
    G = defaultdict(set)
    for i in range(N):
        for j in range(i + 1, N):
            if not (L[i] <= R[j] and L[j] <= R[i]):
                G[i].add(j)
                G[j].add(i)
    weights = [None] * N
    for i in range(N):
        weights[i] = read_input()
    for i in range(Q):
        if s[i] == t[i]:
            write_output(-1)
        else:
            visited = [False] * N
            visited[s[i] - 1] = True
            stack = [s[i] - 1]
            path = [s[i]]
            while stack:
                current = stack.pop()
                if current == t[i] - 1:
                    break
                for neighbor in G[current]:
                    if not visited[neighbor]:
