    L = [int(data[2 * i + 2]) for i in range(N)]
    R = [int(data[2 * i + 3]) for i in range(N)]
    Q = int(data[2 * N + 1])
    queries = []
    for i in range(Q):
        s = int(data[2 * N + 2 * i + 2])
        t = int(data[2 * N + 2 * i + 3])
        queries.append((s, t))
    for s, t in queries:
        print(min_path(W, L, R, s, t))

def min_path(W, L, R, s, t):
    from collections import defaultdict
    from heapq import heappush, heappop
    graph = defaultdict(list)
    for i in range(N):
        for j in range(i + 1, N):
            if not (L[i] <= L[j] <= R[i] or L[i] <= R[j] <= R[i]):
                graph[i].append(j)
                graph[j].append(i)
    visited = [False] * N
    parent = [-1] * N
    distance = [float('inf')] * N
    distance[s - 1] = 0
    heap = [(0, s - 1)]
    while heap:
        d, u = heappop(heap)
        if visited[u]:
            continue
        visited[u] = True
        for v in graph[u]:
            if not visited[v]:
                if distance[u] + W[v] < distance[v]:
                    distance[v] = distance[u] + W[v]
                    parent[v] = u
                    heappush(heap, (distance[v], v))
    if parent[t - 1] == -1:
        return -1
    path = []
    while t != s - 1:
        path.append(t + 1)
        t = parent[t]
    path.append(s)
    return sum(W[i - 1] for i in path[::-1])

