def min_operations(A, B):
    N = len(A)
    squares = [0] * N
    for i in range(N):
        if A[i] == '1':
            squares[i] = 1

    operations = 0
    for i in range(N):
        if squares[i] == 1 and B[i] == '0':
            return -1
        if squares[i] == 0 and B[i] == '1':
            for j in range(i + 1, N):
                if squares[j] == 1:
                    squares[j] = 0
                    operations += 1
                    break
            else:
                return -1

    return operations

